	id_comune_italiano INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT
  , nome_comune TEXT
  , nome_comune_altra_lingua TEXT
  , fk_id_provincia INTEGER
  , fk_id_regioni_italiane INTEGER
  , codice_catastale TEXT
  , FOREIGN KEY (fk_id_provincia) REFERENCES Province_Italiane(id_provincia_italiana)
  , FOREIGN KEY (fk_id_regioni_italiane) REFERENCES Regioni_Italiane(id_regioni_italiane)
);

