--SEZIONE VISTE
CREATE VIEW vw_Comuni_Italiani_dettaglio AS
SELECT c.nome_comune 		AS nome_comune
	 , p.nome_provincia 	AS nome_provincia
 	 , p.abbr_provincia 	AS codice_provincia
  	 , r.nome_regione		AS nome_regione
FROM 		Comuni_Italiani 	c
INNER JOIN 	Province_Italiane 	p ON p.id_provincia_italiana = c.fk_id_provincia
INNER JOIN 	Regioni_Italiane 	r ON r.id_regione_italiana 	= p.fk_id_regioni_italiane;
